import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import './../styles/register.css';

const Register = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {
      const response = await fetch('http://localhost:3000/api/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });

      if (!response.ok) {
        const responseText = await response.text(); // Obtener la respuesta como texto
        try {
          const errorData = JSON.parse(responseText);
          throw new Error(errorData.error || 'Error en el registro');
        } catch {
          throw new Error('Error en el registro: Respuesta no válida');
        }
      }

      const data = await response.json(); // Obtener la respuesta como JSON
      localStorage.setItem('token', data.token);
      navigate('/login');
    } catch (error) {
      setError(error.message || 'No se pudo registrar');
    }
  };

  return (
    <div className="register-container">
      <h1 className="homepage-title">Registro</h1>
      <form className="register-form" onSubmit={handleSubmit}>
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          placeholder="Email"
          required
        />
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Password"
          required
        />
        {error && <p>{error}</p>}
        <button type="submit">Registrarse</button>
        <Link to="/" className="go-home-link">Regresar a la Página de Inicio</Link>
      </form>
    </div>
  );
};

export default Register;
