import React, { useState } from 'react';

const Consulta = () => {
  const [documento, setDocumento] = useState('');
  const [resultado, setResultado] = useState(null);
  const [error, setError] = useState('');

  const validarDocumento = (doc) => {
    const regex = /^\d{6,10}$/;
    const hasRepeatingSequence = /(.)\1{5,}/.test(doc) || /(\d{2,})\1{2,}/.test(doc);
    return regex.test(doc) && !hasRepeatingSequence;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setResultado(null);

    if (!validarDocumento(documento)) {
      setError('El número de documento no es válido.');
      return;
    }

    try {
      const response = await fetch(`https://api.talentotech.cymetria.com/api/v1/blockchain/obtener-estudiantes-aprobados`);
      const data = await response.json();

      const estudiante = data.find(est => est.documento === documento);
      if (estudiante) {
        setResultado(estudiante);
      } else {
        setError('No se encontró un estudiante con ese número de documento.');
      }
    } catch (error) {
      setError('Error al consultar la API.');
    }
  };

  return (
    <div className="container">
      <h2>Consulta de Estudiantes Aprobados</h2>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label>Número de Documento</label>
          <input
            type="text"
            value={documento}
            onChange={(e) => setDocumento(e.target.value)}
            className="form-control"
            required
          />
        </div>
        {error && <div className="alert alert-danger">{error}</div>}
        <button type="submit" className="btn btn-primary">Consultar</button>
      </form>
      {resultado && (
        <div className="mt-4">
          <h4>Resultados</h4>
          <p><strong>Nombre:</strong> {resultado.nombre}</p>
          <p><strong>Documento:</strong> {resultado.documento}</p>
          <p><strong>Email:</strong> {resultado.email}</p>
          <p><strong>Curso:</strong> {resultado.curso}</p>
        </div>
      )}
    </div>
  );
};

export default Consulta;
