// src/components/ConsultaEstudiantes.js
import React, { useState } from 'react';

const ConsultaEstudiantes = () => {
  const [documento, setDocumento] = useState('');
  const [estudiante, setEstudiante] = useState(null);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:2000/api/consulta-estudiante', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': token,
        },
        body: JSON.stringify({ documento }),
      });

      if (!response.ok) {
        throw new Error('Error en la consulta');
      }

      const data = await response.json();
      setEstudiante(data);
    } catch (error) {
      setError('No se pudo realizar la consulta');
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={documento}
          onChange={(e) => setDocumento(e.target.value)}
          placeholder="NÃºmero de Documento"
          required
        />
        <button type="submit">Consultar</button>
      </form>
      {error && <p>{error}</p>}
      {estudiante && (
        <div>
          <h3>Estudiante Aprobado</h3>
          <p>Nombre: {estudiante.nombre}</p>
          <p>Documento: {estudiante.numero_documento}</p>
        </div>
      )}
    </div>
  );
};

export default ConsultaEstudiantes;
